export type MeasurementTypes = 'Centimeters' | "Meters" | "Kg"

export enum PokemonType {
  "Fuego" = "Fuego",
  "Hielo" = "Hielo",
  "Hierva" = "Hierva",
  "Fantasma" = "Fantasma",
  "Volador" = "Volador",
  "Psiquico" = "Psiquico",
  "Normal" = "Normal",
  "Bicho" = "Bicho",
  "Oscuro" = "Oscuro",
  "Veneno" = "Veneno",
  "Roca" = "Roca",
}

export enum PokemonGender {
    "Male" = "Male",
    "Female" = "Female"
}

export interface Measurement {
    value: number,
    measurement: MeasurementTypes
}

export interface Stats {
    hp: number,
    attack: number,
    defense: number,
    specialAttack: number,
    spped: number,
    specialDefense: number
}

export interface Pokemon {
    id: string,
    name: string,
    descripcion?: string,
    gender?: PokemonGender
    type: PokemonType,
    high: Measurement,
    weight: Measurement
    stats?: Stats
}